import React, { useState } from 'react';
import RoomLayout from './components/RoomLayout';
import OutcomeScreen from './components/OutcomeScreen';
import { initialScenario, successScenario, errorScenarios, whiteDoorScenario, greenDoorScenario } from './mock/scenarios';

const App = () => {
  const [currentScenario, setCurrentScenario] = useState(initialScenario);
  const [path, setPath] = useState([]);

  const handleSelectDoor = (doorColor) => {
    const newPath = [...path, doorColor];
    setPath(newPath);

    if (newPath.length === 1) {
      if (newPath[0] === 'red') {
        setCurrentScenario(errorScenarios.initialRedDoor);
      } else if (newPath[0] === 'blue') {
        setCurrentScenario(whiteDoorScenario);
      } else if (newPath[0] === 'green') {
        setCurrentScenario({
          ...whiteDoorScenario,
          description: "Has tomado un atajo. Elige cuidadosamente.",
          doors: [
            { color: "blue", label: "Puerta Azul", disabled: false },
            { color: "red", label: "Puerta Roja", warning: "No cruce por la roja", disabled: false },
            { color: "black", label: "Puerta Negra", disabled: false }
          ]
        });
      }
    } else if (newPath.length === 2) {
      if (newPath[0] === 'blue' && newPath[1] === 'white') {
        setCurrentScenario({
          ...whiteDoorScenario,
          description: "Un cartel dice: 'Elige la puerta roja' pero no hay roja...",
          doors: [
            { color: "white", label: "Puerta Blanca", disabled: true },
            { color: "black", label: "Puerta Negra", disabled: false },
            { color: "green", label: "Puerta Verde", disabled: false }
          ]
        });
      } else if (newPath[1] === 'green') {
        setCurrentScenario(successScenario);
      } else if (newPath[1] === 'black') {
        setCurrentScenario(errorScenarios.noExit);
      } else if (newPath[1] === 'red') {
        setCurrentScenario({
          isSuccess: false,
          title: "Error",
          message: "No lograste encontrar la salida"
        });
      }
    } else if (newPath.length === 3 && newPath[2] === 'green') {
      setCurrentScenario(successScenario);
    } else {
      setCurrentScenario(errorScenarios.wrongPath);
    }
  };

  const resetGame = () => {
    setCurrentScenario(initialScenario);
    setPath([]);
  };

  if (currentScenario.isSuccess !== undefined) {
    return <OutcomeScreen {...currentScenario} />;
  }

  return (
    <RoomLayout
      title={currentScenario.title}
      description={currentScenario.description}
      doors={currentScenario.doors}
      onSelectDoor={handleSelectDoor}
    />
  );
};

export default App;

